'use strict';

import React, { Component } from 'react';
import './App.css';

import {getRandomColor} from './functions/getRandomColor.js';

import {QuoteBox} from './Components/QuoteBox.js';

class App extends Component {
  state= {
    style: {
      backgroundColor: '#80ced6',
    },
  };

  componentDidMount() {
    fetch('https://gist.githubusercontent.com/camperbot/5a022b72e96c4c9585c32bf6a75f62d9/raw/e3c6895ce42069f0ee7e991229064f167fe8ccdc/quotes.json')
      .then(function(response) {
        return response.json();
      })
      .then(function(thisJson)  {
        this.setState(thisJson);
        console.log(thisJson);
      })
  }

  getNewQuote= () => {
    this.setState({
      style: {
        backgroundColor: getRandomColor(),
      },
    });


  }

  render() {
    const backgroundColor= this.state.style.backgroundColor;
    return (
      <div id='app' className="App" style={this.state.style}>
        <QuoteBox color={backgroundColor} onQuoteChange={this.getNewQuote}/>
        <br/>
        <p className='RandomQuoteMachineAuthor'>by Peter Nguyen</p>
      </div>
    );
  }
}

export default App;
